import React, { useState } from 'react';
import { Link } from "react-router-dom";
import { FaUserCircle } from "react-icons/fa"; // Import default profile icon
import Brand from "../assets/Brand-logo.png";
import ProfileIcon from "../assets/profile-icon.png"; // Import profile icon

const Navbar = () => {
  const [profileImg, setProfileImg] = useState(ProfileIcon);

  const handleProfileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setProfileImg(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <nav className="navbar navbar-expand-lg bg-danger">
      <div className="container-fluid">
        {/* Toggle Button for Mobile */}
        <button 
          className="navbar-toggler" 
          type="button" 
          data-bs-toggle="collapse" 
          data-bs-target="#navbarTogglerDemo01" 
          aria-controls="navbarTogglerDemo01" 
          aria-expanded="false" 
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        {/* Logo with Link */}
        <Link to="/" className="navbar-brand d-flex align-items-center">
          <img src={Brand} alt="Brand Logo" className="img-fluid" style={{ height: "50px", width: "auto" }} />
        </Link>

        {/* Collapsible Menu */}
        <div className="collapse navbar-collapse" id="navbarTogglerDemo01">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            <li className="nav-item">
              <Link to="/" className="nav-link active">Home</Link>
            </li>
            <li className="nav-item">
              <Link to="/about" className="nav-link">About</Link>
            </li>
            <li className="nav-item">
              <Link to="/contact" className="nav-link">Contact Us</Link>
            </li>
          </ul>

          {/* Navigation Buttons for Recipes */}
          <div className="d-flex gap-2 ms-auto">
            <Link to="/" className="btn btn-primary">All Recipes</Link>
            <Link to="/saved" className="btn btn-primary">Saved Recipes ❤️</Link>
          </div>

          {/* Search Form */}
          <form className="d-flex ms-3" role="search">
            <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
            <button className="btn btn-outline-light" type="submit">Search</button>
          </form>
          <li className="nav-item">
              <Link to="/login" className="nav-link active">Login</Link>
            </li>
          {/* Profile Picture Icon with Upload Option */}
          <div className="ms-3 position-relative">
            <label htmlFor="profile-upload" className="cursor-pointer">
              {profileImg ? (
                <img src={profileImg} alt="Profile" className="rounded-circle" style={{ height: "40px", width: "40px" }} />
              ) : (
                <FaUserCircle size={40} color="white" />
              )}
            </label>
            <input 
              id="profile-upload" 
              type="file" 
              accept="image/*" 
              onChange={handleProfileChange} 
              className="d-none"
            />
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
