import React, { useState, useEffect } from "react";
import { FaHeart } from "react-icons/fa";
import "bootstrap/dist/css/bootstrap.min.css";

const SavedRecipes = () => {
  const [recipes, setRecipes] = useState([]);
  const savedRecipeIds = JSON.parse(localStorage.getItem("savedRecipes")) || [];

  useEffect(() => {
    fetch("http://localhost:3001/api/recipes")
      .then((res) => res.json())
      .then((data) => {
        const filteredRecipes = data.filter((recipe) => savedRecipeIds.includes(recipe._id));
        setRecipes(filteredRecipes);
      })
      .catch((error) => console.error("Error fetching recipes:", error));
  }, []);

  return (
    <div className="container mt-4">
      <h2>❤️ Saved Recipes</h2>
      {recipes.length === 0 ? (
        <p>No saved recipes yet!</p>
      ) : (
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {recipes.map((recipe) => (
            <div key={recipe._id} className="col">
              <div className="card h-100 shadow-sm">
                <img src={`http://localhost:3001${recipe.image}`} alt={recipe.name} className="card-img-top" />
                <div className="card-body">
                  <h5 className="card-title">{recipe.name}</h5>
                  <p><strong>Ingredients:</strong> {recipe.ingredients.join(", ")}</p>
                  <p><strong>Method:</strong> {recipe.method}</p>
                  <button className="btn btn-danger"><FaHeart /> Liked</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SavedRecipes;
