import React from "react";
import   "./styles/Home.css";
import { Link } from "react-router-dom";
import img1 from "../pages/assets/img1.png";
import img2 from "../pages/assets/img2.png";
import img3 from "../pages/assets/img3.png";
import img4 from "../pages/assets/img4.png";



const ProductCard = ({ image, title, price, altText }) => {
  return (
    <div className="card">
      <img src={image} className="card-img-top" alt={altText} />
      <div className="card-body">
        <h2 className="card-title">{title}</h2>
        <p className="card-text">
          <ul>
            <li>One bag has <b>50kg</b> packing</li>
            <li>Price is <b>Rs. {price}</b></li>
          </ul>
        </p>
        
        {/* Pass selected product details to Order page */}
        <Link to="/order" state={{ product: title, price }}>
          <button className="btn-order">Order Place</button>
        </Link>
        
      </div>
    </div>
  );
};

function Products() {
  return (
    <div className="product-container">
      <ProductCard image={img1} title="Bronze" price={1000} altText="Bronze product" />
      <ProductCard image={img2} title="Silver" price={1200} altText="Silver product" />
      <ProductCard image={img3} title="Gold" price={1500} altText="Gold product" />
      <ProductCard image={img4} title="Platinum" price={1700} altText="Platinum product" />
     
    </div>
  );
}

export default Products;
