import React from "react";
import { FaHeart } from "react-icons/fa";

const SavedRecipes = ({ savedRecipes }) => {
  return (
    <div className="container">
      <h2 className="text-center my-3">❤️ Saved Recipes</h2>

      {savedRecipes.length > 0 ? (
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {savedRecipes.map((recipe) => (
            <div className="col" key={recipe._id}>
              <div className="card h-100 shadow-sm">
                <img
                  src={recipe.image ? `http://localhost:3001${recipe.image}` : "/default-image.jpg"}
                  className="card-img-top"
                  alt={recipe.name}
                  style={{ height: "200px", objectFit: "cover" }}
                />
                <div className="card-body">
                  <h5 className="card-title d-flex justify-content-between align-items-center">
                    {recipe.name}
                    <span style={{ color: "red", fontSize: "1.5rem" }}>
                      <FaHeart />
                    </span>
                  </h5>
                  <p><strong>Ingredients:</strong> {recipe.ingredients.join(", ")}</p>
                  <p><strong>Method:</strong> {recipe.method}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center">No saved recipes yet! ❤️</p>
      )}
    </div>
  );
};

export default SavedRecipes;
