import React, { useState, useEffect } from "react";
import { FaHeart } from "react-icons/fa";

const SavedRecipes = () => {
  const [savedRecipes, setSavedRecipes] = useState([]);
  const [allRecipes, setAllRecipes] = useState([]);

  useEffect(() => {
    // Fetch all recipes from backend
    const fetchRecipes = async () => {
      try {
        const response = await fetch("http://localhost:3000/api/recipes");
        if (!response.ok) throw new Error("Failed to fetch recipes");
        const data = await response.json();
        setAllRecipes(data);
      } catch (error) {
        console.error("Error fetching recipes:", error);
      }
    };

    fetchRecipes();

    // Load saved recipe IDs from localStorage
    const storedRecipes = JSON.parse(localStorage.getItem("savedRecipes")) || [];
    setSavedRecipes(storedRecipes);
  }, []);

  // Ensure savedRecipes are properly matched (string vs. object ID)
  const savedRecipeDetails = allRecipes.filter((recipe) =>
    savedRecipes.includes(recipe._id.toString()) // Ensure correct matching
  );

  return (
    <div className="container">
      <h2 className="text-center my-3">❤️ Saved Recipes</h2>

      {savedRecipeDetails.length > 0 ? (
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {savedRecipeDetails.map((recipe) => (
            <div className="col" key={recipe._id}>
              <div className="card h-100 shadow-sm">
                <img
                  src={recipe.image ? `http://localhost:3000${recipe.image}` : "/default-image.jpg"} // Fallback image
                  className="card-img-top"
                  alt={recipe.name}
                  style={{ height: "200px", objectFit: "cover" }}
                />
                <div className="card-body">
                  <h5 className="card-title d-flex justify-content-between align-items-center">
                    {recipe.name}
                    <span style={{ color: "red", fontSize: "1.5rem" }}>
                      <FaHeart />
                    </span>
                  </h5>
                  <p><strong>Ingredients:</strong> {recipe.ingredients.join(", ")}</p>
                  <p><strong>Method:</strong> {recipe.method}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center">No saved recipes yet! ❤️</p>
      )}
    </div>
  );
};

export default SavedRecipes;
