import React, { useState } from 'react';
import { motion } from 'framer-motion';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    agree: false,
  });

  const handleChange = (e) => {
    const { id, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [id]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
  };

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <motion.form 
        className="row g-3 p-4 bg-light shadow rounded" 
        onSubmit={handleSubmit} 
        initial={{ opacity: 0, y: -50 }} 
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        style={{ maxWidth: "400px", width: "100%" }}
      >
        <h2 className="text-center mb-3">Login</h2>
        <div className="col-12">
          <label htmlFor="email" className="form-label">Email</label>
          <input type="email" className="form-control" id="email" value={formData.email} onChange={handleChange} />
        </div>
        <div className="col-12">
          <label htmlFor="password" className="form-label">Password</label>
          <input type="password" className="form-control" id="password" value={formData.password} onChange={handleChange} />
        </div>
        <div className="col-12 text-end">
          <a href="#" className="text-primary" style={{ fontSize: "0.9rem" }}>Forgot Password?</a>
        </div>
        <div className="col-12">
          <div className="form-check">
            <input className="form-check-input" type="checkbox" id="agree" checked={formData.agree} onChange={handleChange} />
            <label className="form-check-label" htmlFor="agree">
              Agree to terms and conditions
            </label>
          </div>
        </div>
        <div className="col-12">
          <motion.button 
            type="submit" 
            className="btn btn-primary w-100" 
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Login
          </motion.button>
        </div>
      </motion.form>
    </div>
  );
};

export default Login;
