import React, { useState, useEffect } from "react";
import { FaHeart, FaRegHeart, FaPlus, FaEdit, FaTrash, FaTrashRestore } from "react-icons/fa";
import recipesData from "../pages/recipes";
import "../pages/style/Home.css";

const Home = () => {
  const [recipes, setRecipes] = useState(() => JSON.parse(localStorage.getItem("recipes")) || recipesData);
  const [trash, setTrash] = useState(() => JSON.parse(localStorage.getItem("trash")) || []);
  const [search, setSearch] = useState("");
  const [filteredRecipes, setFilteredRecipes] = useState(recipes);
  const [savedRecipes, setSavedRecipes] = useState(() => JSON.parse(localStorage.getItem("savedRecipes")) || []);
  const [showForm, setShowForm] = useState(false);
  const [editIndex, setEditIndex] = useState(null);
  const [newRecipe, setNewRecipe] = useState({
    name: "",
    ingredients: "",
    method: "",
    image: null,
  });

  useEffect(() => {
    localStorage.setItem("recipes", JSON.stringify(recipes));
  }, [recipes]);

  useEffect(() => {
    localStorage.setItem("trash", JSON.stringify(trash));
  }, [trash]);

  useEffect(() => {
    localStorage.setItem("savedRecipes", JSON.stringify(savedRecipes));
  }, [savedRecipes]);

  const handleDeleteRecipe = (index) => {
    const confirmDelete = window.confirm("Are you sure you want to move this recipe to trash?");
    if (confirmDelete) {
      const deletedRecipe = recipes[index];
      setTrash([...trash, deletedRecipe]);
      const updatedRecipes = recipes.filter((_, i) => i !== index);
      setRecipes(updatedRecipes);
      setFilteredRecipes(updatedRecipes);
    }
  };

  const handleRestoreRecipe = (index) => {
    const restoredRecipe = trash[index];
    setRecipes([...recipes, restoredRecipe]);
    setTrash(trash.filter((_, i) => i !== index));
  };

  const handlePermanentDelete = (index) => {
    const confirmDelete = window.confirm("Are you sure you want to permanently delete this recipe?");
    if (confirmDelete) {
      setTrash(trash.filter((_, i) => i !== index));
    }
  };

  return (
    <div className="container">
      <button className="add-recipe-btn" onClick={() => setShowForm(!showForm)}>
        <FaPlus /> {showForm ? "Close" : "Add Recipe"}
      </button>

      <h2>Recipes</h2>
      <div className="row row-cols-1 row-cols-md-3 g-4">
        {filteredRecipes.map((recipe, index) => (
          <div className="col" key={recipe.id}>
            <div className="card h-100 shadow-sm">
              <img src={recipe.image} className="card-img-top" alt={recipe.name} />
              <div className="card-body">
                <h5 className="card-title">{recipe.name}</h5>
                <button className="btn btn-danger btn-sm" onClick={() => handleDeleteRecipe(index)}><FaTrash /></button>
              </div>
            </div>
          </div>
        ))}
      </div>

      <h2>Trash</h2>
      <div className="row row-cols-1 row-cols-md-3 g-4">
        {trash.map((recipe, index) => (
          <div className="col" key={index}>
            <div className="card h-100 shadow-sm">
              <img src={recipe.image} className="card-img-top" alt={recipe.name} />
              <div className="card-body">
                <h5 className="card-title">{recipe.name}</h5>
                <button className="btn btn-success btn-sm" onClick={() => handleRestoreRecipe(index)}><FaTrashRestore /></button>
                <button className="btn btn-danger btn-sm" onClick={() => handlePermanentDelete(index)}><FaTrash /></button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
