import React from "react";

const Home = () => {
  return (
    <div className="flex flex-col items-center justify-center min-h-screen px-4 py-8 md:px-8">
      <h1 className="text-3xl md:text-5xl font-bold text-center mb-6">
        Welcome to the Recipe Sharing Platform
      </h1>
      <p className="text-lg md:text-xl text-gray-600 text-center max-w-2xl">
        Create, share, and manage various recipes with real-time updates.
      </p>
      <button className="mt-6 px-6 py-3 bg-blue-500 text-white rounded-lg text-lg md:text-xl hover:bg-blue-600 transition-all">
        Get Started
      </button>
    </div>
  );
};

export default Home;
