import React, { useState, useEffect } from "react";
import { FaHeart, FaRegHeart, FaPlus, FaEdit, FaTrash } from "react-icons/fa";
import "../pages/style/Home.css";

const Home = () => {
  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState("");
  const [savedRecipes, setSavedRecipes] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editIndex, setEditIndex] = useState(null);
  const [newRecipe, setNewRecipe] = useState({
    name: "",
    ingredients: "",
    method: "",
    image: null,
  });

  // Fetch Recipes from API
  useEffect(() => {
    fetch("http://localhost:5001/recipes")
      .then((res) => res.json())
      .then((data) => setRecipes(data))
      .catch((error) => console.error("Error fetching recipes:", error));
  }, []);

  // Handle Image Upload
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setNewRecipe((prev) => ({ ...prev, image: file }));
      };
      reader.readAsDataURL(file);
    }
  };

  // Save Recipe (POST Request)
  const handleSaveRecipe = async () => {
    if (!newRecipe.name || !newRecipe.ingredients || !newRecipe.method || !newRecipe.image) {
      alert("Please fill out all fields and upload an image before saving!");
      return;
    }

    const formData = new FormData();
    formData.append("name", newRecipe.name);
    formData.append("ingredients", newRecipe.ingredients);
    formData.append("method", newRecipe.method);
    formData.append("image", newRecipe.image);

    try {
      const response = await fetch("http://localhost:5001/recipes", {
        method: "POST",
        body: formData,
      });
      const data = await response.json();
      setRecipes([...recipes, data]);
      setShowForm(false);
      setNewRecipe({ name: "", ingredients: "", method: "", image: null });
    } catch (error) {
      console.error("Error saving recipe:", error);
    }
  };

  // Delete Recipe (DELETE Request)
  const handleDeleteRecipe = async (id) => {
    if (window.confirm("Are you sure you want to delete this recipe?")) {
      try {
        await fetch(`http://localhost:5001/recipes/${id}`, { method: "DELETE" });
        setRecipes(recipes.filter((recipe) => recipe._id !== id));
      } catch (error) {
        console.error("Error deleting recipe:", error);
      }
    }
  };

  // Toggle Save Recipe (Favoriting)
  const toggleSaveRecipe = (id) => {
    setSavedRecipes((prev) =>
      prev.includes(id) ? prev.filter((recipeId) => recipeId !== id) : [...prev, id]
    );
  };

  // Filter Recipes
  const filteredRecipes = recipes.filter(
    (recipe) =>
      recipe.name.toLowerCase().includes(search.toLowerCase()) ||
      recipe.ingredients.some((ingredient) =>
        ingredient.toLowerCase().includes(search.toLowerCase())
      )
  );

  return (
    <div className="container">
      <button className="add-recipe-btn" onClick={() => setShowForm(!showForm)}>
        <FaPlus /> {showForm ? "Close" : "Add Recipe"}
      </button>

      <input
        type="text"
        className="form-control my-3"
        placeholder="Search recipes by name or ingredient..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      {showForm && (
        <div className="card p-3 mb-4">
          <h4>{editIndex !== null ? "Edit Recipe" : "Add a New Recipe"}</h4>
          <input
            type="text"
            className="form-control mb-2"
            placeholder="Dish Name"
            value={newRecipe.name}
            onChange={(e) => setNewRecipe({ ...newRecipe, name: e.target.value })}
          />
          <input
            type="text"
            className="form-control mb-2"
            placeholder="Ingredients (comma separated)"
            value={newRecipe.ingredients}
            onChange={(e) => setNewRecipe({ ...newRecipe, ingredients: e.target.value })}
          />
          <textarea
            className="form-control mb-2"
            placeholder="Method"
            value={newRecipe.method}
            onChange={(e) => setNewRecipe({ ...newRecipe, method: e.target.value })}
          />
          <input type="file" className="form-control mb-2" onChange={handleImageUpload} accept="image/*" />
          <button className="btn btn-success" onClick={handleSaveRecipe}>
            {editIndex !== null ? "Update" : "Add"} Recipe
          </button>
        </div>
      )}

      <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
        {filteredRecipes.length > 0 ? (
          filteredRecipes.map((recipe) => (
            <div className="col" key={recipe._id}>
              <div className="card h-100 shadow-sm">
                <img src={`http://localhost:5001${recipe.image}`} className="card-img-top" alt={recipe.name} />
                <div className="card-body">
                  <h5 className="card-title d-flex justify-content-between align-items-center">
                    {recipe.name}
                    <span onClick={() => toggleSaveRecipe(recipe._id)} style={{ cursor: "pointer", color: "red", fontSize: "1.5rem" }}>
                      {savedRecipes.includes(recipe._id) ? <FaHeart /> : <FaRegHeart />}
                    </span>
                  </h5>
                  <p><strong>Ingredients:</strong> {recipe.ingredients.join(", ")}</p>
                  <p><strong>Method:</strong> {recipe.method}</p>
                  <button className="btn btn-danger btn-sm" onClick={() => handleDeleteRecipe(recipe._id)}>
                    <FaTrash />
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center">No matching recipes found.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
