import React, { useState, useEffect } from "react";
import { FaHeart, FaRegHeart, FaPlus } from "react-icons/fa";
import recipesData from "../pages/recipes"; // Ensure correct path
import "../pages/style/Home.css";

const Home = () => {
  const [savedRecipes, setSavedRecipes] = useState([]);
  const [recipes, setRecipes] = useState(recipesData);
  const [search, setSearch] = useState("");
  const [filteredRecipes, setFilteredRecipes] = useState(recipesData);
  const [showForm, setShowForm] = useState(false); // Form visibility

  // New Recipe State
  const [newRecipe, setNewRecipe] = useState({
    name: "",
    ingredients: "",
    method: "",
    image: null,
  });

  useEffect(() => {
    const storedRecipes = JSON.parse(localStorage.getItem("savedRecipes")) || [];
    setSavedRecipes(storedRecipes);
  }, []);

  useEffect(() => {
    localStorage.setItem("savedRecipes", JSON.stringify(savedRecipes));
  }, [savedRecipes]);

  const toggleSaveRecipe = (id) => {
    setSavedRecipes((prev) =>
      prev.includes(id) ? prev.filter((recipeId) => recipeId !== id) : [...prev, id]
    );
  };

  // 🔍 Search & Filter Recipes
  useEffect(() => {
    setFilteredRecipes(
      recipes.filter((recipe) =>
        recipe.name.toLowerCase().includes(search.toLowerCase()) ||
        recipe.ingredients.some((ingredient) =>
          ingredient.toLowerCase().includes(search.toLowerCase())
        )
      )
    );
  }, [search, recipes]);

  // 📷 Handle Image Upload
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setNewRecipe((prev) => ({ ...prev, image: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  // 🆕 Add New Recipe
  const handleAddRecipe = () => {
    if (!newRecipe.name || !newRecipe.ingredients || !newRecipe.method || !newRecipe.image) {
      alert("Please fill out all fields and upload an image before adding a recipe!");
      return;
    }

    const newRecipeObj = {
      id: recipes.length + 1,
      name: newRecipe.name,
      ingredients: newRecipe.ingredients.split(",").map((item) => item.trim()), // Convert to array
      method: newRecipe.method,
      image: newRecipe.image, // User-uploaded image
    };

    setRecipes([...recipes, newRecipeObj]); // Add new recipe
    setFilteredRecipes([...recipes, newRecipeObj]); // Update filtered list

    // Clear form and hide it
    setNewRecipe({ name: "", ingredients: "", method: "", image: null });
    setShowForm(false);
  };

  return (
    <div className="container">
      {/* Left-Side Floating Button */}
      <button className="add-recipe-btn" onClick={() => setShowForm(!showForm)}>
        <FaPlus /> {showForm ? "Close" : "Add Recipe"}
      </button>

      {/* Search Bar */}
      <input
        type="text"
        className="form-control my-3"
        placeholder="Search recipes by name or ingredient..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      {/* ➕ Add New Recipe Form (Toggled) */}
      {showForm && (
        <div className="card p-3 mb-4">
          <h4>Add a New Recipe</h4>
          <input
            type="text"
            className="form-control mb-2"
            placeholder="Dish Name"
            value={newRecipe.name}
            onChange={(e) => setNewRecipe({ ...newRecipe, name: e.target.value })}
          />
          <input
            type="text"
            className="form-control mb-2"
            placeholder="Ingredients (comma separated)"
            value={newRecipe.ingredients}
            onChange={(e) => setNewRecipe({ ...newRecipe, ingredients: e.target.value })}
          />
          <textarea
            className="form-control mb-2"
            placeholder="Method"
            value={newRecipe.method}
            onChange={(e) => setNewRecipe({ ...newRecipe, method: e.target.value })}
          />

          {/* Image Upload */}
          <input type="file" className="form-control mb-2" onChange={handleImageUpload} accept="image/*" />
          {newRecipe.image && (
            <img src={newRecipe.image} alt="Preview" className="img-thumbnail mb-2" style={{ maxHeight: "150px" }} />
          )}

          <button className="btn btn-success" onClick={handleAddRecipe}>Add Recipe</button>
        </div>
      )}

      {/* Recipe Cards */}
      <div className="row row-cols-1 row-cols-md-3 g-4">
        {filteredRecipes.length > 0 ? (
          filteredRecipes.map((recipe) => (
            <div className="col" key={recipe.id}>
              <div className="card h-100 shadow-sm">
                <img
                  src={recipe.image}
                  className="card-img-top"
                  alt={recipe.name}
                  style={{ height: "200px", objectFit: "cover" }}
                />
                <div className="card-body">
                  <h5 className="card-title d-flex justify-content-between align-items-center">
                    {recipe.name}
                    <span
                      onClick={() => toggleSaveRecipe(recipe.id)}
                      style={{ cursor: "pointer", color: "red", fontSize: "1.5rem" }}
                    >
                      {savedRecipes.includes(recipe.id) ? <FaHeart /> : <FaRegHeart />}
                    </span>
                  </h5>
                  {/* Ingredients */}
                  <p className="card-text">
                    <strong>Ingredients:</strong> {recipe.ingredients.join(", ")}
                  </p>

                  {/* Recipe Method */}
                  <p className="card-text">
                    <strong>Method:</strong> {recipe.method}
                  </p>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center">No matching recipes found.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
