import React, { useState } from "react";
import img1 from "../assets/img1.png";
import img2 from "../assets/img2.png";
import img3 from "../assets/img3.png";
import img4 from "../assets/img4.png";
import img5 from "../assets/img5.png";
import img6 from "../assets/img6.png";
import { FaHeart, FaRegHeart } from "react-icons/fa"; // Import heart icons

const recipes = [
  {
    id: 1,
    name: "Chicken Tandoor",
    image: img1,
    ingredients: ["Chicken", "Yogurt", "Spices", "Lemon"],
  },
  {
    id: 2,
    name: "Momos",
    image: img2,
    ingredients: ["Flour", "Chicken/Veg Filling", "Soy Sauce", "Garlic"],
  },
  {
    id: 3,
    name: "Badam Shekh",
    image: img3,
    ingredients: ["Almonds", "Milk", "Sugar", "Cardamom"],
  },
  {
    id: 4,
    name: "Chicken Soup",
    image: img4,
    ingredients: ["Chicken", "Carrots", "Onion", "Garlic"],
  },
  {
    id: 5,
    name: "Noodles",
    image: img5,
    ingredients: ["Noodles", "Soy Sauce", "Veggies", "Garlic"],
  },
  {
    id: 6,
    name: "Veg Soup",
    image: img6,
    ingredients: ["Carrots", "Tomato", "Peas", "Ginger"],
  },
];

const Home = () => {
  const [savedRecipes, setSavedRecipes] = useState([]);

  // Toggle save/unsave recipe
  const toggleSaveRecipe = (id) => {
    setSavedRecipes((prev) =>
      prev.includes(id) ? prev.filter((recipeId) => recipeId !== id) : [...prev, id]
    );
  };

  return (
    <div className="row row-cols-1 row-cols-md-3 g-4 m-2">
      {recipes.map((recipe) => (
        <div className="col" key={recipe.id}>
          <div className="card h-100">
            <img src={recipe.image} className="card-img-top" alt={recipe.name} />
            <hr />
            <div className="card-body">
              <h5 className="card-title d-flex justify-content-between align-items-center">
                {recipe.name}
                <span onClick={() => toggleSaveRecipe(recipe.id)} style={{ cursor: "pointer", color: "red" }}>
                  {savedRecipes.includes(recipe.id) ? <FaHeart /> : <FaRegHeart />}
                </span>
              </h5>
              <p className="card-text">
                <strong>Ingredients:</strong> {recipe.ingredients.join(", ")}
              </p>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Home;
