import React, { useState, useEffect } from "react";
import { FaHeart } from "react-icons/fa";
import "bootstrap/dist/css/bootstrap.min.css";
import { useMemo } from "react";

const SavedRecipes = () => {
  const [recipes, setRecipes] = useState([]);

  const savedRecipeIds = useMemo(() => JSON.parse(localStorage.getItem("savedRecipes")) || [], []);
  
  useEffect(() => {
    const fetchSavedRecipes = async () => {
      try {
        const response = await fetch("http://localhost:3001/api/recipes");
        const data = await response.json();
        const filteredRecipes = data.filter((recipe) => savedRecipeIds.includes(recipe._id));
        setRecipes(filteredRecipes);
      } catch (error) {
        console.error("Error fetching recipes:", error);
      }
    };
  
    fetchSavedRecipes();
  }, [savedRecipeIds]); // ✅ Added savedRecipeIds as a dependency
  

  return (
    <div className="container mt-4">
      <h2>❤️ Saved Recipes</h2>
      {recipes.length === 0 ? (
        <p>No saved recipes yet!</p>
      ) : (
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {recipes.map((recipe) => (
            <div key={recipe._id} className="col">
              <div className="card h-100 shadow-sm">
                <img src={`http://localhost:3001${recipe.image}`} alt={recipe.name} className="card-img-top" />
                <div className="card-body">
                  <h5 className="card-title">{recipe.name}</h5>
                  <p><strong>Ingredients:</strong> {recipe.ingredients.join(", ")}</p>
                  <p><strong>Method:</strong> {recipe.method}</p>
                  <button className="btn btn-danger"><FaHeart /> Liked</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SavedRecipes;
