import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import Brand from "../assets/Brand-logo.png";
import ProfileIcon from "../assets/profile-icon.png";

const Navbar = () => {
  const [profileImg, setProfileImg] = useState(localStorage.getItem("profileImg") || ProfileIcon);

  useEffect(() => {
    localStorage.setItem("profileImg", profileImg);
  }, [profileImg]);

  const handleProfileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setProfileImg(e.target.result);
        localStorage.setItem("profileImg", e.target.result); // Save to localStorage
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <nav className="navbar navbar-expand-lg bg-danger navbar-dark">
      <div className="container-fluid">
        {/* Toggle Button for Mobile */}
        <button 
          className="navbar-toggler" 
          type="button" 
          data-bs-toggle="collapse" 
          data-bs-target="#navbarNav" 
          aria-controls="navbarNav" 
          aria-expanded="false" 
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        {/* Logo */}
        <Link to="/" className="navbar-brand">
          <img src={Brand} alt="Brand Logo" className="img-fluid" style={{ height: "50px" }} />
        </Link>

        {/* Collapsible Menu */}
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav me-auto">
            <li className="nav-item">
              <Link to="/" className="nav-link active">Home</Link>
            </li>
            <li className="nav-item">
              <Link to="/about" className="nav-link">About</Link>
            </li>
            <li className="nav-item">
              <Link to="/contact" className="nav-link">Contact Us</Link>
            </li>
          </ul>

          {/* Navigation Buttons */}
          <div className="d-flex gap-2">
            <Link to="/" className="btn btn-primary">All Recipes</Link>
            <Link to="/saved" className="btn btn-primary">Saved Recipes ❤️</Link>
          </div>

          {/* Search Bar */}
          <form className="d-flex ms-3">
            <input className="form-control me-2" type="search" placeholder="Search" />
            <button className="btn btn-outline-light" type="submit">Search</button>
          </form>

          {/* Login Button */}
          <Link to="/login" className="btn btn-light ms-3">Login</Link>

          {/* Profile Picture */}
          <div className="ms-3 position-relative">
            <label htmlFor="profile-upload" className="cursor-pointer">
              <img src={profileImg} alt="Profile" className="rounded-circle" style={{ height: "40px", width: "40px" }} />
            </label>
            <input 
              id="profile-upload" 
              type="file" 
              accept="image/*" 
              onChange={handleProfileChange} 
              className="d-none"
            />
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
