import React, { useState, useEffect } from "react";
import { FaHeart, FaRegHeart, FaPlus, FaTrash, FaEdit } from "react-icons/fa";
import "../pages/style/Home.css";

const Home = () => {
  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState("");
  const [savedRecipes, setSavedRecipes] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editRecipeId, setEditRecipeId] = useState(null);
  const [newRecipe, setNewRecipe] = useState({
    name: "",
    ingredients: "",
    method: "",
    image: null,
  });

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch("http://localhost:5002/api/recipes");
        if (!response.ok) throw new Error("Failed to fetch recipes");
        const data = await response.json();
        setRecipes(data);
      } catch (error) {
        console.error("Error fetching recipes:", error);
      }
    };
    fetchRecipes();
    
    const storedRecipes = JSON.parse(localStorage.getItem("savedRecipes")) || [];
    setSavedRecipes(storedRecipes);
  }, []);

  const handleImageUpload = (e) => {
    setNewRecipe((prev) => ({ ...prev, image: e.target.files[0] }));
  };

  const handleSaveRecipe = async () => {
    if (!newRecipe.name || !newRecipe.ingredients || !newRecipe.method) {
      alert("Please fill out all fields before saving!");
      return;
    }

    const formData = new FormData();
    formData.append("name", newRecipe.name);
    formData.append("ingredients", JSON.stringify(newRecipe.ingredients.split(",")));
    formData.append("method", newRecipe.method);
    if (newRecipe.image) formData.append("image", newRecipe.image);

    const token = localStorage.getItem("token");
    if (!token) {
      console.error("No token found, authorization denied");
      return;
    }

    try {
      const response = await fetch(
        editRecipeId ? `http://localhost:5002/api/recipes/${editRecipeId}` : "http://localhost:5002/api/recipes",
        {
          method: editRecipeId ? "PUT" : "POST",
          headers: {
            Authorization: `Bearer ${token}`,
          },
          body: formData,
        }
      );

      if (!response.ok) throw new Error("Failed to save recipe");
      const data = await response.json();
      setRecipes((prevRecipes) =>
        editRecipeId ? prevRecipes.map((r) => (r._id === editRecipeId ? data : r)) : [...prevRecipes, data]
      );
      setShowForm(false);
      setEditRecipeId(null);
      setNewRecipe({ name: "", ingredients: "", method: "", image: null });
    } catch (error) {
      console.error("Error saving recipe:", error);
    }
  };

  const handleDeleteRecipe = async (id) => {
    if (!window.confirm("Are you sure you want to delete this recipe?")) return;
    try {
      const response = await fetch(`http://localhost:5002/api/recipes/${id}`, { method: "DELETE" });
      if (!response.ok) throw new Error("Failed to delete recipe");
      setRecipes((prevRecipes) => prevRecipes.filter((recipe) => recipe._id !== id));
    } catch (error) {
      console.error("Error deleting recipe:", error);
    }
  };

  const handleEditRecipe = (recipe) => {
    setEditRecipeId(recipe._id);
    setNewRecipe({
      name: recipe.name,
      ingredients: recipe.ingredients.join(", "),
      method: recipe.method,
      image: null,
    });
    setShowForm(true);
  };

  const toggleSaveRecipe = (id) => {
    const updatedSavedRecipes = savedRecipes.includes(id)
      ? savedRecipes.filter((recipeId) => recipeId !== id)
      : [...savedRecipes, id];
    setSavedRecipes(updatedSavedRecipes);
    localStorage.setItem("savedRecipes", JSON.stringify(updatedSavedRecipes));
  };

  return (
    <div className="container">
      <button className="add-recipe-btn" onClick={() => setShowForm(!showForm)}>
        <FaPlus /> {showForm ? "Close" : "Add Recipe"}
      </button>

      <input
        type="text"
        className="form-control my-3"
        placeholder="Search recipes by name or ingredient..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      {showForm && (
        <div className="card p-3 mb-4">
          <h4>{editRecipeId ? "Edit Recipe" : "Add a New Recipe"}</h4>
          <input
            type="text"
            className="form-control mb-2"
            placeholder="Dish Name"
            value={newRecipe.name}
            onChange={(e) => setNewRecipe({ ...newRecipe, name: e.target.value })}
          />
          <input
            type="text"
            className="form-control mb-2"
            placeholder="Ingredients (comma separated)"
            value={newRecipe.ingredients}
            onChange={(e) => setNewRecipe({ ...newRecipe, ingredients: e.target.value })}
          />
          <textarea
            className="form-control mb-2"
            placeholder="Method"
            value={newRecipe.method}
            onChange={(e) => setNewRecipe({ ...newRecipe, method: e.target.value })}
          />
          <input type="file" className="form-control mb-2" onChange={handleImageUpload} accept="image/*" />
          <button className="btn btn-success" onClick={handleSaveRecipe}>
            {editRecipeId ? "Update" : "Add"} Recipe
          </button>
        </div>
      )}

      <div className="row">
        {recipes.map((recipe) => (
          <div className="col" key={recipe._id}>
            <div className="card">
              <div className="card-body">
                <h5>{recipe.name}</h5>
                <p><strong>Ingredients:</strong> {recipe.ingredients.join(", ")}</p>
                <p><strong>Method:</strong> {recipe.method}</p>
                <button className="btn btn-warning btn-sm me-2" onClick={() => handleEditRecipe(recipe)}>
                  <FaEdit />
                </button>
                <button className="btn btn-danger btn-sm" onClick={() => handleDeleteRecipe(recipe._id)}>
                  <FaTrash />
                </button>
                <span onClick={() => toggleSaveRecipe(recipe._id)} style={{ cursor: "pointer", color: "red" }}>
                  {savedRecipes.includes(recipe._id) ? <FaHeart /> : <FaRegHeart />}
                </span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
