import React, { useState, useEffect, useMemo } from "react";
import { FaHeart, FaRegHeart, FaPlus, FaEdit, FaTrash } from "react-icons/fa";
import recipesData from "../pages/recipes";
import "../pages/style/Home.css";

const Home = () => {
  const [recipes, setRecipes] = useState(() => JSON.parse(localStorage.getItem("recipes")) || recipesData);
  const [search, setSearch] = useState("");
  const [savedRecipes, setSavedRecipes] = useState(() => JSON.parse(localStorage.getItem("savedRecipes")) || []);
  const [showForm, setShowForm] = useState(false);
  const [editIndex, setEditIndex] = useState(null);
  const [newRecipe, setNewRecipe] = useState({ name: "", ingredients: "", method: "", image: null });

  // Save recipes to localStorage when recipes change
  useEffect(() => {
    localStorage.setItem("recipes", JSON.stringify(recipes));
  }, [JSON.stringify(recipes)]);

  // Save savedRecipes to localStorage when savedRecipes change
  useEffect(() => {
    localStorage.setItem("savedRecipes", JSON.stringify(savedRecipes));
  }, [JSON.stringify(savedRecipes)]);

  // Filter recipes using useMemo for better performance
  const filteredRecipes = useMemo(() => {
    return recipes.filter(
      (recipe) =>
        recipe.name.toLowerCase().includes(search.toLowerCase()) ||
        recipe.ingredients.some((ingredient) => ingredient.toLowerCase().includes(search.toLowerCase()))
    );
  }, [search, recipes]);

  // Handle input changes for the form
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewRecipe((prev) => ({ ...prev, [name]: value }));
  };

  // Handle image upload
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setNewRecipe((prev) => ({ ...prev, image: reader.result }));
      reader.readAsDataURL(file);
    }
  };

  // Save or update a recipe
  const handleSaveRecipe = () => {
    if (!newRecipe.name || !newRecipe.ingredients || !newRecipe.method || !newRecipe.image) {
      alert("Please fill out all fields and upload an image before saving!");
      return;
    }

    setRecipes((prevRecipes) => {
      if (editIndex !== null) {
        const updatedRecipes = [...prevRecipes];
        updatedRecipes[editIndex] = { ...newRecipe, id: prevRecipes[editIndex].id };
        return updatedRecipes;
      } else {
        return [...prevRecipes, { id: Date.now(), ...newRecipe, ingredients: newRecipe.ingredients.split(",").map((item) => item.trim()) }];
      }
    });

    setNewRecipe({ name: "", ingredients: "", method: "", image: null });
    setEditIndex(null);
    setShowForm(false);
  };

  // Delete a recipe
  const handleDeleteRecipe = (index) => {
    if (window.confirm("Are you sure you want to delete this recipe?")) {
      setRecipes((prevRecipes) => prevRecipes.filter((_, i) => i !== index));
    }
  };

  // Toggle save/unsave recipe
  const toggleSaveRecipe = (id) => {
    setSavedRecipes((prev) => (prev.includes(id) ? prev.filter((recipeId) => recipeId !== id) : [...prev, id]));
  };

  return (
    <div className="container">
      <button className="btn btn-primary my-3" onClick={() => setShowForm(!showForm)}>
        <FaPlus /> {showForm ? "Close" : "Add Recipe"}
      </button>

      <input
        type="text"
        className="form-control my-3"
        placeholder="Search recipes by name or ingredient..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      {showForm && (
        <div className="card p-3 mb-4">
          <h4>{editIndex !== null ? "Edit Recipe" : "Add a New Recipe"}</h4>
          <input type="text" className="form-control mb-2" placeholder="Dish Name" name="name" value={newRecipe.name} onChange={handleChange} />
          <input type="text" className="form-control mb-2" placeholder="Ingredients (comma separated)" name="ingredients" value={newRecipe.ingredients} onChange={handleChange} />
          <textarea className="form-control mb-2" placeholder="Method" name="method" value={newRecipe.method} onChange={handleChange} />
          <input type="file" className="form-control mb-2" onChange={handleImageUpload} accept="image/*" />
          {newRecipe.image && <img src={newRecipe.image} alt="Preview" className="img-thumbnail mb-2" style={{ maxWidth: "100%", height: "auto" }} />}
          <button className="btn btn-success" onClick={handleSaveRecipe}>{editIndex !== null ? "Update" : "Add"} Recipe</button>
        </div>
      )}

      <div className="row row-cols-1 row-cols-md-3 g-4">
        {filteredRecipes.length > 0 ? (
          filteredRecipes.map((recipe, index) => (
            <div className="col" key={recipe.id}>
              <div className="card h-100 shadow-sm">
                <img src={recipe.image} className="card-img-top" alt={recipe.name} style={{ width: "100%", height: "auto", objectFit: "contain" }} />
                <div className="card-body">
                  <h5 className="card-title d-flex justify-content-between align-items-center">
                    {recipe.name}
                    <span onClick={() => toggleSaveRecipe(recipe.id)} style={{ cursor: "pointer", color: "red", fontSize: "1.5rem" }}>
                      {savedRecipes.includes(recipe.id) ? <FaHeart /> : <FaRegHeart />}
                    </span>
                  </h5>
                  <p><strong>Ingredients:</strong> {recipe.ingredients.join(", ")}</p>
                  <p><strong>Method:</strong> {recipe.method}</p>
                  <button className="btn btn-warning btn-sm me-2" onClick={() => { setNewRecipe(recipe); setEditIndex(index); setShowForm(true); }}><FaEdit /></button>
                  <button className="btn btn-danger btn-sm" onClick={() => handleDeleteRecipe(index)}><FaTrash /></button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center">No matching recipes found.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
