import React, { useState, useEffect } from "react";
import { FaHeart, FaRegHeart, FaPlus, FaEdit, FaTrash } from "react-icons/fa";
import "../pages/style/Home.css";
import SavedRecipes from "..//SavedRecipes";

const Home = () => {
  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState("");
  const [savedRecipes, setSavedRecipes] = useState(JSON.parse(localStorage.getItem("savedRecipes")) || []);
  const [showForm, setShowForm] = useState(false);
  const [editRecipeId, setEditRecipeId] = useState(null);
  const [newRecipe, setNewRecipe] = useState({ name: "", ingredients: "", method: "", image: null });

  useEffect(() => {
    fetch("http://localhost:3001/api/recipes")
      .then((res) => res.json())
      .then((data) => setRecipes(data))
      .catch((error) => console.error("Error fetching recipes:", error));
  }, []);

  useEffect(() => {
    localStorage.setItem("savedRecipes", JSON.stringify(savedRecipes));
  }, [savedRecipes]);

  const handleImageUpload = (e) => {
    setNewRecipe((prev) => ({ ...prev, image: e.target.files[0] }));
  };

  const handleSaveRecipe = async () => {
    if (!newRecipe.name || !newRecipe.ingredients || !newRecipe.method) {
      alert("Please fill out all fields!");
      return;
    }

    const formData = new FormData();
    formData.append("name", newRecipe.name);
    formData.append("ingredients", JSON.stringify(newRecipe.ingredients.split(",")));
    formData.append("method", newRecipe.method);
    if (newRecipe.image) {
      formData.append("image", newRecipe.image);
    }

    try {
      let response;
      if (editRecipeId) {
        response = await fetch(`http://localhost:3001/api/recipes/${editRecipeId}`, {
          method: "PUT",
          body: formData,
        });
      } else {
        response = await fetch("http://localhost:3001/api/recipes", {
          method: "POST",
          body: formData,
        });
      }

      if (!response.ok) throw new Error("Failed to save recipe");

      const data = await response.json();
      setRecipes(editRecipeId ? recipes.map((r) => (r._id === editRecipeId ? data : r)) : [...recipes, data]);
      setShowForm(false);
      setEditRecipeId(null);
      setNewRecipe({ name: "", ingredients: "", method: "", image: null });
    } catch (error) {
      console.error("Error saving recipe:", error);
    }
  };

  const handleEditRecipe = (recipe) => {
    setEditRecipeId(recipe._id);
    setNewRecipe({
      name: recipe.name,
      ingredients: recipe.ingredients.join(", "),
      method: recipe.method,
      image: null,
    });
    setShowForm(true);
  };

  const handleDeleteRecipe = async (id) => {
    if (!window.confirm("Are you sure you want to delete this recipe?")) return;

    try {
      await fetch(`http://localhost:3001/api/recipes/${id}`, { method: "DELETE" });
      setRecipes((prev) => prev.filter((recipe) => recipe._id !== id));
    } catch (error) {
      console.error("Error deleting recipe:", error);
    }
  };

  const toggleFavorite = (id) => {
    if (savedRecipes.includes(id)) {
      setSavedRecipes(savedRecipes.filter((recipeId) => recipeId !== id));
    } else {
      setSavedRecipes([...savedRecipes, id]);
    }
  };

  return (
    <div className="container">
      <button className="add-recipe-btn" onClick={() => setShowForm(!showForm)}>
        <FaPlus /> {showForm ? "Close" : "Add Recipe"}
      </button>

      <input
        type="text"
        className="form-control my-3"
        placeholder="Search recipes by name or ingredient..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      {showForm && (
        <div className="card p-3 mb-4">
          <h4>{editRecipeId ? "Edit Recipe" : "Add a New Recipe"}</h4>
          <input type="text" className="form-control mb-2" placeholder="Dish Name" value={newRecipe.name} onChange={(e) => setNewRecipe({ ...newRecipe, name: e.target.value })} />
          <input type="text" className="form-control mb-2" placeholder="Ingredients (comma separated)" value={newRecipe.ingredients} onChange={(e) => setNewRecipe({ ...newRecipe, ingredients: e.target.value })} />
          <textarea className="form-control mb-2" placeholder="Method" value={newRecipe.method} onChange={(e) => setNewRecipe({ ...newRecipe, method: e.target.value })} />
          <input type="file" className="form-control mb-2" onChange={handleImageUpload} accept="image/*" />
          <button className="btn btn-success" onClick={handleSaveRecipe}>{editRecipeId ? "Update" : "Add"} Recipe</button>
        </div>
      )}

      <div className="row row-cols-1 row-cols-md-3 g-4">
        {recipes.map((recipe) => (
          <div key={recipe._id} className="col">
            <div className="card">
              <img src={`http://localhost:3001${recipe.image}`} alt={recipe.name} />
              <h5>{recipe.name}</h5>
              <p><strong>Ingredients:</strong> {recipe.ingredients.join(", ")}</p>
              <p><strong>Method:</strong> {recipe.method}</p>
              <div className="card-actions">
                <button onClick={() => toggleFavorite(recipe._id)} className="favorite-btn">
                  {savedRecipes.includes(recipe._id) ? <FaHeart color="red" /> : <FaRegHeart />}
                </button>
                <button onClick={() => handleEditRecipe(recipe)}><FaEdit /></button>
                <button onClick={() => handleDeleteRecipe(recipe._id)}><FaTrash /></button>
              </div>
            </div>
          </div>
        ))}
      </div>
      
      <SavedRecipes savedRecipes={recipes.filter(recipe => savedRecipes.includes(recipe._id))} />
    </div>
  );
};

export default Home;
