import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Register = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  // const handleSubmit = async (e) => {
  //   e.preventDefault();

  //   try {
  //     const res = await fetch("http://localhost:5001/api/auth/register", {
  //       method: "POST",
  //       headers: { "Content-Type": "application/json" },
  //       body: JSON.stringify(formData),
  //     });

  //     const data = await res.json();

  //     if (!res.ok) {
  //       alert(data.message || "Registration failed");
  //     } else {
  //       alert("✅ Registration Successful! Please log in.");
  //       navigate("/login");
  //     }
  //   } catch (error) {
  //     console.error("Error during registration:", error);
  //   }
  // };


  const handleSubmit = async (e) => {
    e.preventDefault();
  
    try {
      const res = await fetch("http://localhost:5001/api/auth/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
  
      console.log("Response status:", res.status); // Debugging step
      console.log("Response headers:", res.headers.get("content-type")); // Check if it's JSON
  
      const text = await res.text(); // Read raw response
      console.log("Raw response:", text); // Debugging step
  
      try {
        const data = JSON.parse(text); // Try to parse as JSON
        if (!res.ok) {
          alert(data.message || "Registration failed");
        } else {
          alert("✅ Registration Successful! Please log in.");
          navigate("/login");
        }
      } catch (jsonError) {
        console.error("JSON parsing error:", jsonError);
        alert("Error: Server response is not valid JSON!");
      }
    } catch (error) {
      console.error("Error during registration:", error);
      alert("An error occurred. Please try again.");
    }
  };
  


  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="row g-3 p-4 bg-light shadow rounded" style={{ maxWidth: "400px", width: "100%" }}>
        <h2 className="text-center mb-3">Register</h2>
        <form onSubmit={handleSubmit}>
          <div className="col-12">
            <label htmlFor="name" className="form-label">Name</label>
            <input type="text" className="form-control" id="name" value={formData.name} onChange={handleChange} required />
          </div>
          <div className="col-12">
            <label htmlFor="email" className="form-label">Email</label>
            <input type="email" className="form-control" id="email" value={formData.email} onChange={handleChange} required />
          </div>
          <div className="col-12">
            <label htmlFor="password" className="form-label">Password</label>
            <input type="password" className="form-control" id="password" value={formData.password} onChange={handleChange} required />
          </div>
          <div className="col-12">
            <button type="submit" className="btn btn-primary w-100">Register</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Register;
