import React, { useState, useEffect } from "react";
import { FaHeart, FaRegHeart } from "react-icons/fa";
import recipes from "../pages/recipes"; // Ensure this path is correct

const Home = () => {
  const [savedRecipes, setSavedRecipes] = useState([]);
  const [search, setSearch] = useState("");
  const [filteredRecipes, setFilteredRecipes] = useState(recipes);

  useEffect(() => {
    const storedRecipes = JSON.parse(localStorage.getItem("savedRecipes")) || [];
    setSavedRecipes(storedRecipes);
  }, []);

  useEffect(() => {
    localStorage.setItem("savedRecipes", JSON.stringify(savedRecipes));
  }, [savedRecipes]);

  const toggleSaveRecipe = (id) => {
    setSavedRecipes((prev) =>
      prev.includes(id) ? prev.filter((recipeId) => recipeId !== id) : [...prev, id]
    );
  };

  // 🔍 Search & Filter Recipes
  useEffect(() => {
    setFilteredRecipes(
      recipes.filter((recipe) =>
        recipe.name.toLowerCase().includes(search.toLowerCase()) ||
        recipe.ingredients.some((ingredient) => ingredient.toLowerCase().includes(search.toLowerCase()))
      )
    );
  }, [search]);

  return (
    <div className="container">
      {/* Search Bar */}
      <input
        type="text"
        className="form-control my-3"
        placeholder="Search recipes by name or ingredient..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      <div className="row row-cols-1 row-cols-md-3 g-4">
        {filteredRecipes.length > 0 ? (
          filteredRecipes.map((recipe) => (
            <div className="col" key={recipe.id}>
              <div className="card h-100 shadow-sm">
                <img src={recipe.image} className="card-img-top" alt={recipe.name} style={{ height: "200px", objectFit: "cover" }} />
                <div className="card-body">
                  <h5 className="card-title d-flex justify-content-between align-items-center">
                    {recipe.name}
                    <span onClick={() => toggleSaveRecipe(recipe.id)} style={{ cursor: "pointer", color: "red", fontSize: "1.5rem" }}>
                      {savedRecipes.includes(recipe.id) ? <FaHeart /> : <FaRegHeart />}
                    </span>
                  </h5>
                  {/* Ingredients */}
                  <p className="card-text"><strong>Ingredients:</strong> {recipe.ingredients.join(", ")}</p>
                  
                  {/* Recipe Method / Instructions */}
                  <p className="card-text"><strong>Method:</strong> {recipe.method}</p> 
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center">No matching recipes found.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
