import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";

const Home = () => {


// Establish connection with the backend server
const socket = io("http://localhost:5000");

const RecipeSharingApp = () => {
  // State to store the list of recipes
  const [recipes, setRecipes] = useState([]);
  // State to handle new recipe input
  const [newRecipe, setNewRecipe] = useState({ title: "", description: "" });

  // Effect to listen for real-time updates from the server
  useEffect(() => {
    socket.on("updateRecipes", (updatedRecipes) => {
      setRecipes(updatedRecipes);
    });
    // Cleanup listener on component unmount
    return () => socket.off("updateRecipes");
  }, []);

  // Handle form input changes
  const handleChange = (e) => {
    setNewRecipe({ ...newRecipe, [e.target.name]: e.target.value });
  };

  // Submit new recipe to the server
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!newRecipe.title || !newRecipe.description) return;
    socket.emit("addRecipe", newRecipe);
    setNewRecipe({ title: "", description: "" });
  };

  return (
    <div className="p-4 max-w-2xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Recipe Sharing Platform</h1>
      {/* Form to add new recipes */}
      <form onSubmit={handleSubmit} className="mb-4">
        <Input
          type="text"
          name="title"
          placeholder="Recipe Title"
          value={newRecipe.title}
          onChange={handleChange}
          className="mb-2"
        />
        <Textarea
          name="description"
          placeholder="Recipe Description"
          value={newRecipe.description}
          onChange={handleChange}
          className="mb-2"
        />
        <Button type="submit">Add Recipe</Button>
      </form>
      {/* Displaying the list of recipes */}
      <div className="grid gap-4">
        {recipes.map((recipe, index) => (
          <Card key={index} className="p-4 border rounded-lg shadow-md">
            <CardContent>
              <h2 className="text-lg font-semibold">{recipe.title}</h2>
              <p>{recipe.description}</p>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}
};
export default Home
