import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Register from "./pages/Register";
import Login from "./pages/Login";
import Home from "./pages/Home";
import Navbar from "./components/Navbar";
import SavedRecipes from "./pages/SavedRecipes";

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem("token"));

  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsAuthenticated(!!token); // Update auth state on token change
  }, []);

  const handleLogin = (token) => {
    localStorage.setItem("token", token);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsAuthenticated(false);
  };

  return (
    <Router>
      <Navbar isAuthenticated={isAuthenticated} setIsAuthenticated={setIsAuthenticated} />

      <Routes>
        {/* Public Routes */}
        {!isAuthenticated && <Route path="/register" element={<Register />} />}
        {!isAuthenticated && <Route path="/login" element={<Login onLogin={handleLogin} />} />}

        {/* Protected Routes */}
        {isAuthenticated && <Route path="/home" element={<Home />} />}
        {isAuthenticated && <Route path="/saved" element={<SavedRecipes />} />}

        {/* Default Route Handling */}
        <Route
          path="*"
          element={<Navigate to={isAuthenticated ? "/home" : "/login"} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
